    public boolean validaDataNascimento(String dataNascimento){
        Pattern ptr = Pattern.compile("^(0[1-9]|[12][0-9]|3[01])/(0[1-9]|1[012])/[12][0-9]{3}$");
        return ptr.matcher(dataNascimento).matches();
    }
    
    public boolean validaCpf(String cpf){
        Pattern ptr = Pattern.compile("^\\d{3}\\x2E\\d{3}\\x2E\\d{3}\\x2D\\d{2}$");
        return ptr.matcher(cpf).matches();
    }
    
    public boolean validaCnpj(String cnpj){
        Pattern ptr = Pattern.compile("[0-9]{2}\\.?[0-9]{3}\\.?[0-9]{3}\\/?[0-9]{4}\\-?[0-9]{2}");
        return ptr.matcher(cnpj).matches();
    }
    
    public boolean validaDdd(String ddd){
        return !ddd.equals("(  )");
        
    }
    
    public boolean validaTelefone(String telefone){
       return telefone.length() >= 8 && telefone.length() <= 9;
    }
    
    public boolean validaCep(String cep){
        return !cep.equals("     -   ");
    }
    
    public boolean validaEndereco(String endereco){
        return endereco.length() > 4;
    }
    
    public boolean validaNumero(String numero){
        return numero.length() > 1;
    }
    
    
    public boolean validaCombo(int index){
        return index != -1;
    }
    
    public boolean validaUsuario(String usuario){
        return usuario.length() > 5;
    }
    
    /*Need to Change - Implement Regex - Hash*/
    public boolean validaSenha(String senha){
        return senha.length() > 5;
    }
    
    public boolean validaPreco(String preco){
        return !preco.equals("R$   ,  ");
    }
    
    public boolean validaImagem(String imagem){
        return imagem == null;
    }
    
    public boolean validaTexto(String texto){
        return texto.length() > 8;
    }
    
    public boolean validaCodigoBarras(String codigoBarras){
        return !codigoBarras.equals("         ");
    }
