    public boolean validateDataNascimento(String dataNascimento){
        Pattern ptr = Pattern.compile("^(0[1-9]|[12][0-9]|3[01])/(0[1-9]|1[012])/[12][0-9]{3}$");
        return ptr.matcher(dataNascimento).matches();
    }
    
    public boolean validateCpf(String cpf){
        Pattern ptr = Pattern.compile("^\\d{3}\\x2E\\d{3}\\x2E\\d{3}\\x2D\\d{2}$");
        return ptr.matcher(cpf).matches();
    }
    
    public boolean validateDdd(String ddd){
        return !ddd.equals("(  )");
        
    }
    
    public boolean validateTelefone(String telefone){
       return telefone.length() >= 8 && telefone.length() < 9;
    }
    
    public boolean validateCep(String cep){
        return !cep.equals("     -   ");
    }
    
    public boolean validateEndereco(String endereco){
        return endereco.length() > 4;
    }
    
    public boolean validateNumero(String numero){
        return numero.length() > 1;
    }
    
    
    public boolean validateCombo(int index){
        return index != -1;
    }
    
    public boolean validateUsuario(String usuario){
        return usuario.length() > 5;
    }
    
    /*Need to Change - Implement Regex - Hash*/
    public boolean validateSenha(String senha){
        return senha.length() > 5;
    }
